/* Responsive */

/* Definimos los cambios para responsive teléfonos en posición apaisada a tables en vertical */

@media screen and (max-width: 800px) {
  /* Transición menú de navegación a menú hamburguesa */

  .nav__menu-enlace {
    display: none;
  }

  .nav__menu-enlace.icon {
    float: right;
    display: block;
    font-size: 2vw;
  }

  .nav__menu.responsive {
    position: relative;
  }

  .nav__menu.responsive .icon {
    position: absolute;
    right: 0;
    top: 0;
  }

  .nav__menu.responsive a {
    float: none;
    display: block;
    text-align: center;
    font-size: 2vw;
    margin: 0.5% 0;
  }

  .nav__menu-enlace:hover {
    background-color: #2196f3;
    color: yellow;
  }

  .respon {
    position: relative;
    width: 30%;
    left: 55%;
  }

  /* poner los div de la cabecera en modo vertical */

  .header-container {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    background-color: white;
    flex-wrap: wrap;
  }

  /* Cambio de formato del title de la página */

  .header_title {
    order: 1;
    position: relative;
    font-size: 7vw;
    width: 90%;
    right: 0;
    text-align: center;
  }

  /* ordenar los div según el orden especificado en los requisitos */

  .header__logo {
    order: 2;
    width: 60%;
    left: 10%;
  }

  .header__img1 {
    width: 25%;
  }

  .header__img2 {
    width: 7%;
  }

  .header__img3 {
    width: 10em;
  }
  @-webkit-keyframes logoBook{
    0%{
      opacity: 0;
      top: 0em;
      left: 0%;


    }
    25% {
      opacity: 0.5;
      left: 5%;
    }

    50%{
      opacity: 0.9;
      left: 15%;

    }
    100%{
      opacity: 1;
      left: 20%;
      top: 0em;

    }
  }
  @keyframes logoBook{
    0%{
      opacity: 0;
      top: 0em;
      left: 0%;


    }
    25% {
      opacity: 0.5;
      left: 5%;
    }

    50%{
      opacity: 0.9;
      left: 15%;

    }
    100%{
      opacity: 1;
      left: 20%;
      top: 0em;

    }
  }

  @-webkit-keyframes logoGlasses{
    0%{
      opacity: 0;
      left: 10%;
    }
    25% {
      opacity: 0.5;
      left: 15%;
    }

    50%{
      opacity: 9;
      left: 19%;
    }
    100%{
      left: 13%;
      top: 13em;
    }
  }

  @keyframes logoGlasses{
    0%{
      opacity: 0;
      left: 10%;
    }
